var searchButton=document.querySelector(".show-modal-button"),searchForm=document.querySelector(".search-form"),arrivalDate=document.querySelector("#arrival-date"),departureDate=document.querySelector("#departure-date"),adult=document.querySelector("#adult"),children=document.querySelector("#children");searchForm.classList.add("modal-hide"),searchButton.addEventListener("click",function(e){e.preventDefault(),searchForm.classList.contains("modal-show")?(searchForm.classList.remove("modal-show"),searchForm.classList.add("modal-hide")):(searchForm.classList.remove("modal-hide"),searchForm.classList.add("modal-show"))}),searchForm.addEventListener("submit",function(e){arrivalDate.value&&departureDate.value&&adult.value||(e.preventDefault(),searchForm.classList.add("modal-error"))}),window.addEventListener("keydown",function(e){27===e.keyCode&&searchForm.classList.contains("modal-show")&&(e.preventDefault(),searchForm.classList.remove("modal-show"),searchForm.classList.remove("modal-error"))});